(function() {
    'use strict';

    // Your code here...
    var location=window.location
    if(location.href.search("cart.jd.com")!=-1){
        //购物车页面
        //进页面自检
        setTimeout(function (){ //先等dom加载完毕
            var selectAll=document.getElementsByClassName("select-all");
            if(selectAll.length<=0){
                //限流了 等10s再刷新试试
                setTimeout(function () {
                    location.href="https://cart.jd.com";
                },10000);
            }
            else{
             //正常工作，自动开启捡漏
            var zdxd=document.getElementById("AiceOsm");
            if(zdxd&&zdxd.checked==false){
                zdxd.click();//自动下单
            }
            var jlmode=document.getElementById("AiceJlmode");
            if(jlmode&& jlmode.checked==false){
                jlmode.click();//捡漏模式
            }
            var freq=document.getElementById("AiceCyce");
            if(freq){
                freq.value='1000'; //1000ms频率 防止限流
            }
            var start =document.getElementById("AiceStart");
            if(start.textContent=='开始倒计时'){ //如果没有点击，则点击
                start.click();
            }

            setTimeout(function (){
                 location.href="https://cart.jd.com"; //每过20min重新加载一次 及时发现限流问题
            },1000*60*20);

            }

        },3000);

    }
    else if(location.href.search("trade.jd.com/shopping/order")!=-1){
        //下订单页面
        //点击提交订单

        window.addEventListener('load', function() {
            var submitBtn0= document.getElementById("order-submit");
            if(submitBtn0){
                submitBtn0.click();
            }
        }, false);

        setInterval(function(){
            var submitBtn1= document.getElementById("order-submit");
            if(submitBtn1){
                submitBtn1.click();
            }
        }(),100);

        axios.get('http://localhost:8080/email/plus') //提醒放货
            .then(response => (console.log(response)))
            .catch(function (error) { // 请求失败处理
            console.log(error);
        });

        setTimeout(function (){
            location.href="https://cart.jd.com"; //跳转回购物车页面
        },5000);//定时器，3s后如果还在当前页面就是没抢到，回到购物车页面
    }
    else if(location.href.search("payc.m.jd.com")!=-1){
        //下订单成功页面 发邮件提醒付款
        axios.get('http://localhost:8080/email/send')
            .then(response => (console.log(response)))
            .catch(function (error) { // 请求失败处理
            console.log(error);
        });

       axios.get('http://localhost:8080/email/send')
            .then(response => (console.log(response)))
            .catch(function (error) { // 请求失败处理
            console.log(error);
        });

    }
    else if(location.href.search("item.jd.com")!=-1){
        //商品详情页面，修改按钮，实现无货强制加购功能
       //交给另外的脚本处理了
//        window.addEventListener('load', function() {
//            let item_id = window.location.href.match(/.*\/(\d+).html.*?/)[1];
//            let cartButton = document.getElementById('InitCartUrl');
//            if (cartButton.href.indexOf("#none") != -1 ){
//                cartButton.href = 'https://cart.jd.com/gate.action?pid=' + item_id + '&pcount=1&ptype=1'
//                cartButton.setAttribute("class",'btn-special1 btn-lg');
//            }
//        }, false);

    }
    else{
        //登录token失效，提醒重新登录
      //  axios.get('http://localhost:8080/email/login')
       //     .then(response => (console.log(response)))
       //     .catch(function (error) { // 请求失败处理
      //      console.log(error);
      //  });
          location.href="https://cart.jd.com"; //有些情况下单失败会跳转首页，此时需要重新回到购物车页面
    }



})();